mapper.select:
 	SELECT
		COUNT(T1.id)
		FROM
			admin_permission T1
		WHERE
		 1=1
 			
			 AND T1.id = #{id}
			
			
			 AND T1.`name` = #{name}
			
			
			 AND T1.sort = #{sort}
			
			
			 AND T1.url = #{url}
			
			
			 AND T1.parent_id = #{parentId}
			
			
			 AND T1.parent_id = ''

 	SELECT
	T1.id AS id,
	T1.`name` AS name,
	T1.sort AS sort,
	T1.url AS url
	FROM
		admin_permission T1
	WHERE
		1=1
		
		
		 AND T1.id = #{id}
		
		
		 AND T1.`name` = #{name}
		
		
		 AND T1.sort = #{sort}
		
		
		 AND T1.url = #{url}
		
		
		 AND T1.parent_id = #{parentId}
		
		
		 AND T1.parent_id = ''
		
		ORDER BY T1.sort
		
			Limit #{begin},#{pageSize}
