mapper.select:
   		SELECT
			COUNT(T1.user_id)
		FROM
			admin_user T1
		WHERE
			1 = 1
        
           AND  T1.role_id =#{roleId}
        
         
           AND  T1.T1.username =#{username}
        
         
           AND  T1.locked =#{locked}

SELECT T1.username as username, T1.role_id as roleId, T1.locked as locked, T1.user_id as userId FROM admin_user T1 WHERE 1 = 1
AND T1.role_id =#{roleId}
AND T1.T1.username =#{username}
AND T1.locked =#{locked}
ORDER BY T1.username
Limit #{begin},#{pageSize}
